// --- This Code Node extracts 'content' from multiple input items ---

// Step 1: Use .map() to iterate over ALL incoming items ($input.all()).
// For each item, access its 'json' property, and then the 'content' field within that.
const contentArray = $input.all().map(item => {
  // Basic safety check: ensure item.json and item.json.content exist.
  // Return null or an empty string if not found, otherwise return the content.
  // Adjust the fallback value (null) if needed.
  return item.json?.content ?? null;
});

// Step 2: Filter out any potential null values if an item was missing content (optional)
// If you are certain all items will have content, you can skip this filter.
const validContentArray = contentArray.filter(content => content !== null);

// Step 3: Return the result as a *single* new n8n item.
// This item contains your final array of strings under the 'documents' key.
return [{
  json: {
    // Use validContentArray if you filtered, otherwise use contentArray
    documents: validContentArray
    // documents: contentArray // <-- Use this if you didn't filter
  }
}];

// --- Code Node to Reorder Items Based on Rerank Results ---

// --- Step 1: Get Data from Input Nodes ---
// Using the input lines confirmed by the user as correct for their workflow.

// Get the data array to be reordered from the 'Setup Array' node.
// This could be an array of objects OR an array of strings.
const originalDataItems = $('Create Array').first().json.documents;

// Get the rerank results array from the primary input node's first item.
// (Assumed to be the Cohere Rerank node output).
const rerankOrderInfo = $input.first().json.results;

// --- Step 2: Validate Inputs (Basic Checks) ---
// Validate that we actually got arrays to work with.
if (!Array.isArray(originalDataItems)) {
  throw new Error("Data retrieved from $('Setup Array').first().json.documents is not an array. Check the 'Setup Array' node output.");
}
if (!Array.isArray(rerankOrderInfo) || rerankOrderInfo.length === 0) {
  // Allow rerankOrderInfo to be empty if originalDataItems is also empty
  if (originalDataItems.length !== 0) {
      throw new Error("Could not get valid rerank results from the input node ($input.first()), but original data exists. Check the node providing rerank data.");
  }
  // If both are empty, allow it to proceed and return empty.
}


// --- Step 3: Create the New Array in the Reranked Order ---
// Handle the case where inputs might be empty.
let sortedData = [];
if (rerankOrderInfo && rerankOrderInfo.length > 0 && originalDataItems && originalDataItems.length > 0) {
    // Iterate through the rerankOrderInfo array. Each element tells us
    // the index of the item we want from the originalDataItems array.
    sortedData = rerankOrderInfo.map(rankInfo => {
      const originalIndex = rankInfo.index;

      // Check if the index is valid for the originalDataItems array
      if (originalIndex !== undefined && originalIndex !== null && originalIndex >= 0 && originalIndex < originalDataItems.length) {
        // Retrieve the corresponding original data item (object or string)
        return originalDataItems[originalIndex];
      } else {
        // Log error for invalid index found in rerank results
        console.error(`Error: Rerank result index ${originalIndex} is invalid or out of bounds for original data (length ${originalDataItems.length}). Skipping this index.`);
        return null; // Return null for invalid indices
      }
    }).filter(item => item !== null); // Remove any nulls caused by invalid indices
} else {
    // If inputs were empty or invalid, ensure sortedData is an empty array
    sortedData = [];
    console.log("Input data (original or rerank info) is empty or invalid, resulting in empty sorted output.");
}


// --- Step 4: Return the Sorted Data as a Single Item Containing the Array ---
// This format returns ONE n8n item. The item's json property contains
// an object with a key 'sortedDocuments' which holds the array.
// This structure works correctly whether sortedData contains objects or strings.
return [{
  json: {
    sortedDocuments: sortedData
  }
}];

const text = $input.first().json.content;
const chunkSize = 1000;
const chunkOverlap = 0;
const overlapBoundary = 'word'; // 'word' or 'sentence'

// Prioritize splitting by Markdown headings, then paragraphs, then lines.
const separators = [
    "\n# ", // H1
    "\n## ", // H2
    "\n### ", // H3
    "\n#### ", // H4
    "\n##### ", // H5
    "\n###### ", // H6
    "\n\n", // Paragraph
    "\n",   // Line
    " ",    // Word
    ""      // Character
];

/**
 * Adjusts the overlap text so it starts at a word or sentence boundary.
 *
 * @param {string} prevChunk - The previous chunk of text.
 * @param {number} overlap - The desired minimum number of characters to overlap.
 * @param {string} type - 'word' or 'sentence'.
 * @returns {string} The adjusted overlap text.
 */
function getOverlapBoundary(prevChunk, overlap, type) {
    let candidateStart = prevChunk.length - overlap;
    if (candidateStart < 0) candidateStart = 0;

    if (type === 'sentence') {
        const substring = prevChunk.slice(candidateStart);
        const sentenceRegex = /[.!?]\s*/g;
        let lastIndex = -1;
        let match;

        while ((match = sentenceRegex.exec(substring)) !== null) {
            lastIndex = match.index + match[0].length;
        }

        if (lastIndex !== -1 && lastIndex < substring.length) {
            return prevChunk.slice(candidateStart + lastIndex);
        }
    }

    // Fallback for 'word' boundary or if no sentence terminator is found.
    while (candidateStart > 0 && !/\s/.test(prevChunk[candidateStart - 1])) {
        candidateStart--;
    }
    return prevChunk.slice(candidateStart);
}

/**
 * Recursively splits the text into chunks of a maximum length, applying overlap.
 * It prioritizes splitting based on the order of separators provided.
 *
 * @param {string} text - The text to split.
 * @param {number} chunkSize - The maximum length of each chunk.
 * @param {number} overlap - The number of characters to overlap between chunks.
 * @param {string[]} seps - Array of separators to use for splitting.
 * @returns {string[]} An array of text chunks.
 */
function recursiveSplit(text, chunkSize, overlap, seps) {
    const finalChunks = [];
    let currentChunk = "";

    // Start with the highest-priority separator
    const separator = seps[0];
    const remainingSeps = seps.slice(1);

    // Split by the current separator
    const splits = text.split(separator);

    for (let i = 0; i < splits.length; i++) {
        const part = splits[i];

        // Add the separator back to the part (except for the first part)
        const partWithSeparator = (i > 0) ? separator + part : part;

        // If the part fits in the current chunk, add it
        if (currentChunk.length + partWithSeparator.length <= chunkSize) {
            currentChunk += partWithSeparator;
        } else {
            // If the current chunk has content, push it to the results
            if (currentChunk.length > 0) {
                finalChunks.push(currentChunk);
            }

            // If the new part itself is too large, split it further recursively
            if (partWithSeparator.length > chunkSize) {
                // Remove the separator for the recursive call if it was added
                const partToSplit = (i > 0) ? part : partWithSeparator;
                const subChunks = recursiveSplit(partToSplit, chunkSize, overlap, remainingSeps);
                finalChunks.push(...subChunks);
                currentChunk = ""; // Reset current chunk
            } else {
                currentChunk = partWithSeparator;
            }
        }
    }

    // Add the last remaining chunk
    if (currentChunk.length > 0) {
        finalChunks.push(currentChunk);
    }

    // If we have successfully split the text, apply overlap and return
    if (finalChunks.length > 1) {
        const overlappedChunks = finalChunks.map((chunk, i) => {
            if (i > 0 && overlap > 0) {
                const prevChunk = finalChunks[i - 1];
                const overlapText = getOverlapBoundary(prevChunk, overlap, overlapBoundary);
                return overlapText + chunk;
            }
            return chunk;
        });
        return overlappedChunks;
    }

    // If no splitting occurred with the current separator, try the next one
    if (remainingSeps.length > 0) {
        return recursiveSplit(text, chunkSize, overlap, remainingSeps);
    }

    // If no separators can split the text (e.g., text is smaller than chunkSize), return as a single chunk
    return [text];
}

return recursiveSplit(text, chunkSize, chunkOverlap, separators)
  .map(chunk => ({ json: { chunk } }));

