// Loop through each input item
for (const item of items) {
  const data = item.json;

  const key = data.metadata_name;
  const values = data.allowed_values;

  output += `## ${key}\n`;
  output += `The filter key ${key} can have the following possible values\n\n`;
  output += `${values.trim()}\n\n`;

  fieldList += `"${key}":"<ADD>",\n`;
}

// Remove trailing comma and newline from fieldList
fieldList = fieldList.trim().replace(/,\s*$/, '');

// Take the first item and modify it with aggregated data
const firstItem = items[0];
firstItem.json.filterPromptInstructions = output.trim();
firstItem.json.list_of_fields = fieldList;

// Return only the first item (now containing aggregated data)
return [firstItem];

